qemu-system-aarch64 -machine virt -cpu max -smp 1 -m 1G -kernel linux-arm64/arch/arm64/boot/Image.gz -drive if=none,format=qcow2,file=alpine_tcg/rootfs.qcow2,id=hd0 -device virtio-blk-device,drive=hd0 -append "root=/dev/vda rw rootfstype=ext4 console=ttyAMA0 TERM=asni" -nographic -netdev user,id=eth0 -device virtio-net-device,netdev=eth0 -fsdev local,security_model=mapped-file,id=fsdev0,path=./share -device virtio-9p-pci,id=fs0,fsdev=fsdev0,mount_tag=hostshare